import { BaseProps } from '../../base/props';
import { PositionY } from '../../base/atoms';
export interface BaseSticky<CONTAINER = any> extends BaseProps {
    /**
    * 容器的 ref
    */
    container: CONTAINER
    /**
    * 吸附位置
    */
    position: PositionY
    /**
    * 距离，当 position 为 top 时，设置的是 top
    */
    threshold: number
    /**
    * 吸附时的层级
    */
    zIndex: number
    /**
    * 吸附状态改变时触发
    */
    onChange: (val: boolean) => void
}
